import { useState } from 'react';
import './App.css';
import Axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState(0);
  const [empleadosList, setEmpleados] = useState([]);

  const add = () => {
    Axios.post('http://localhost:3001/create',{
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: anios
    }).then(() => {
      alert('Empleado registrado con exito');
    });
  }

  const getEmpleados = () => {
    Axios.get('http://localhost:3001/empleados').then((response) => {
      setEmpleados(response.data);
      alert("Obteniendo")
    });
  }



  return (
    <div class="container">
    <div className="datos">
      <div>
      <div class="card text-center">
      <div class="card-header">
        Featured
      </div>
      <div class="card-body">
      <div class="input-group mb-3">
        <span class="input-group-text" id="basic-addon1">Nombre: </span>
        <input type="text"
        onChange={(event) => {
          setNombre(event.target.value);
        }}
        className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
      </div>
        <label>Nombre:  <input 

          type = "text"/></label> <br/>
          <label>Edad: <input 
          onChange={(event) => {
            setEdad(event.target.value);
          }}
          type = "number"/></label><br/>

          <label>País:  <input 
          onChange={(event) => {
            setPais(event.target.value);
          }}
          type = "text"/></label><br/>

          <label>Cargo: <input 
          onChange={(event) => {
            setCargo(event.target.value);
          }}
          type = "text"/></label><br/>

          <label>Años: <input 
          onChange={(event) => {
            setAnios(event.target.value);
          }}
          type = "number"/></label><br/>
        </div>
        <div class="card-header">
          <button className='btn btn-success' onClick={add}>Registrar</button>
        </div>
      </div>
    </div>

      </div>
        <div className='lista'>
          <button className='btn btn-success' onClick={getEmpleados}>Listar Empleados</button>
          {
            empleadosList.map((val, key)=> {
              return <div className=''>{val.nombre}</div>
            })
          }
          </div> 
    </div>
  );
}



export default App;
