      alert("Obteniendo")
    });
  }

  const mostrarDatos = ()=>{
    alert(nombre);
  }

  return (
    <div className="datos">
      <div>
        <label>Nombre:  <input 
        onChange={(event) => {
          setNombre(event.target.value);
        }}
        type = "text"/></label> <br/>
        <label>Edad: <input 
        onChange={(event) => {
          setEdad(event.target.value);
        }}
        type = "number"/></label><br/>


        <label>País:  <input 
        onChange={(event) => {
          setPais(event.target.value);
        }}
        type = "text"/></label><br/>

        <label>Cargo: <input 
        onChange={(event) => {
          setCargo(event.target.value);
        }}
        type = "text"/></label><br/>

        <label>Años: <input 
        onChange={(event) => {
          setAnios(event.target.value);
        }}
        type = "number"/></label><br/>
        <button onClick={add}>Registrar</button>
      </div>
    </div>
  );
}



export default App;
