import { useState } from 'react';
import './App.css';
import Axios from "axios";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState(0);
  const [empleadosList, setEmpleados] = useState([]);

  const add = () => {
    Axios.post('http://localhost:3001/create',{
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: anios
    }).then(() => {
      alert('Empleado registrado con exito');
    });
  }

  const getEmpleados = () => {
    Axios.get('http://localhost:3001/empleados').then((response) => {
      setEmpleados(response.data);
      alert("Obteniendo")
    });
  }



  return (
    <div className="container">
      <div className="datos">
        <div className="card text-center">
          <div className="card-header">
            GESTIÓN DE EMPLEADOS
          </div>
          <div className="card-body">
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Nombre: </span>
            <input type="text"
            onChange={(event) => {
              setNombre(event.target.value);
            }}
            className="form-control"  aria-label="Username" aria-describedby="basic-addon1"/>
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Edad: </span>
            <input type="number"
            onChange={(event) => {
              setEdad(event.target.value);
            }}
            className="form-control" aria-label="Username" aria-describedby="basic-addon1"/>
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">País: </span>
            <input type="text"
            onChange={(event) => {
              setPais(event.target.value);
            }}
            className="form-control"  aria-label="Username" aria-describedby="basic-addon1"/>
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Cargo: </span>
            <input type="text"
            onChange={(event) => {
              setCargo(event.target.value);
            }}
            className="form-control"  aria-label="Username" aria-describedby="basic-addon1"/>
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Años: </span>
            <input type="number"
            onChange={(event) => {
              setAnios(event.target.value);
            }}
            className="form-control"  aria-label="Username" aria-describedby="basic-addon1"/>
          </div>
          </div>
          <div class="card-header">
            <button className='btn btn-success' onClick={add}>Registrar</button>
          </div>
        </div>
      </div>      
        <button className='btn btn-success' onClick={getEmpleados}>Listar Empleados</button>
      <table class="table table-striped">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Nombre</th>
      <th scope="col">Edad</th>
      <th scope="col">País</th>
      <th scope="col">Cargo</th>
      <th scope="col">Experiencia</th>
    </tr>
  </thead>
  <tbody>
  {
              empleadosList.map((val, key)=> {
                return <tr>
                <th scope="row">{val.id}</th>
                <td>{val.nombre}</td>
                <td>{val.edad}</td>
                <td>{val.pais}</td>
                <td>{val.cargo}</td>
                <td>{val.anios}</td>
              </tr>
              })
        }
  </tbody>
</table>
    </div> 
  );

}



export default App;
