  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState(0);

  const add = () => {
    Axios.post('http://localhost:3001/create')
  }
  return (
    <div className="datos">
      <div>
        <label>Nombre:  <input type = "text"/></label> <br/>
        <label>Edad: <input type = "text"/></label><br/>
